import React, { useState } from "react";

function Counter() {
  // Declares a new state variable, which we will call "count"
  // Declares a setCount function that allows you to modify the count value
  const[count, setCount] = useState(0);
  const[startValue, setStartValue] = useState("");
  const display = (e) => {
      e.preventDefault();
      {count};
  }

  return (
    <div>
        <input placeholder="Add a start value" type="number"/>
      <p onChange={display}>The counter is at : {count} </p>
      <button onClick={() => setCount(count + 1)}>
        +1
      </button>
      <button onClick={() => setCount(count - 1)}>
        -1
      </button>
    </div>
  );
}

export default Counter;
