import React, { useState } from "react";

function Counter() {
  // Declares a new state variable, which we will call "count"
  // Declares a setCount function that allows you to modify the count value
  const[count, setCount] = useState(0);
  const[startNumber, setStartNumber] = useState(5)

  return (
    <div>
        <input placeholder="Add a start value" type="number"/>
        <button>
            Set the start number
        </button>
        <p>The counter is at : {count} </p>
        <button onClick={() => setCount(count + 1)}>
            +1
        </button>
        <button onClick={() => setCount(count - 1)}>
            -1
        </button>
    </div>
  );
}

export default Counter;
